import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    adminInfo: null,
    token: localStorage.getItem('admin-token') || '',
    chargingPiles: [],
    vehicleQueue: [],
    statistics: null
  },
  mutations: {
    SET_ADMIN_INFO(state, adminInfo) {
      state.adminInfo = adminInfo
    },
    SET_TOKEN(state, token) {
      state.token = token
      localStorage.setItem('admin-token', token)
    },
    SET_CHARGING_PILES(state, piles) {
      state.chargingPiles = piles
    },
    SET_VEHICLE_QUEUE(state, queue) {
      state.vehicleQueue = queue
    },
    SET_STATISTICS(state, stats) {
      state.statistics = stats
    }
  },
  actions: {
    // 登录
    async login({ commit }, adminInfo) {
      // 这里应该调用登录API
      const token = 'mock-admin-token' // 模拟token
      commit('SET_TOKEN', token)
      commit('SET_ADMIN_INFO', adminInfo)
    },
    // 登出
    logout({ commit }) {
      commit('SET_TOKEN', '')
      commit('SET_ADMIN_INFO', null)
      localStorage.removeItem('admin-token')
    },
    // 获取充电桩状态
    async getChargingPiles({ commit }) {
      // 这里应该调用API获取充电桩状态
      const mockPiles = [
        { id: 'A', type: 'fast', status: 'working', totalCharges: 100, totalTime: 500, totalPower: 3000 },
        { id: 'B', type: 'fast', status: 'working', totalCharges: 90, totalTime: 450, totalPower: 2700 },
        { id: 'C', type: 'slow', status: 'working', totalCharges: 150, totalTime: 750, totalPower: 1050 },
        { id: 'D', type: 'slow', status: 'working', totalCharges: 140, totalTime: 700, totalPower: 980 },
        { id: 'E', type: 'slow', status: 'working', totalCharges: 130, totalTime: 650, totalPower: 910 }
      ]
      commit('SET_CHARGING_PILES', mockPiles)
    }
  },
  getters: {
    isLoggedIn: state => !!state.token,
    adminInfo: state => state.adminInfo,
    chargingPiles: state => state.chargingPiles,
    vehicleQueue: state => state.vehicleQueue,
    statistics: state => state.statistics
  }
})
