mapper.insert:
INSERT INTO charging_detail (
    order_id, user_id, pile_id, charging_time, charging_amount, electricity_fee, service_fee, total_fee, status, create_time, update_time
) VALUES (
    #{orderId}, #{userId}, #{pileId}, #{chargingTime}, #{chargingAmount}, #{electricityFee}, #{serviceFee}, #{totalFee}, #{status}, NOW(), NOW()
)

mapper.select:
SELECT 
FROM charging_detail
WHERE user_id = #{userId}
AND DATE(create_time) = #{date}

SELECT 
FROM charging_detail
WHERE pile_id = #{pileId}
AND DATE(create_time) = #{date}
